import {
    precompileTemplate
} from "@ember/template-compilation";
export default precompileTemplate("{{#unless this.selectKit.isHidden}}\n  {{component this.selectKit.options.headerComponent tabindex=this.tabindex value=this.value selectedContent=this.selectedContent selectKit=this.selectKit id=(concat this.selectKit.uniqueID \"-header\")}}\n\n  <SelectKit::SelectKitBody @selectKit={{this.selectKit}} @id={{concat this.selectKit.uniqueID \"-body\"}}>\n    {{component this.selectKit.options.filterComponent selectKit=this.selectKit id=(concat this.selectKit.uniqueID \"-filter\")}}\n\n    {{#each this.collections as |collection|}}\n      {{component (component-for-collection collection.identifier this.selectKit) collection=collection selectKit=this.selectKit value=this.value}}\n    {{/each}}\n\n    {{#if this.selectKit.filter}}\n      {{#if (and this.selectKit.hasNoContent (not this.selectKit.isLoading))}}\n        <span class=\"no-content\" role=\"alert\">\n          {{i18n \"select_kit.no_content\"}}\n        </span>\n      {{else}}\n        <span class=\"results-count\" role=\"alert\">\n          {{i18n \"select_kit.results_count\" count=this.mainCollection.length}}\n        </span>\n      {{/if}}\n    {{/if}}\n\n  </SelectKit::SelectKitBody>\n{{/unless}}", {
    moduleName: "select-kit/components/single-select.hbs"
});