import {
    precompileTemplate
} from "@ember/template-compilation";
export default precompileTemplate("<div class=\"select-kit-header-wrapper\">\n  {{#each this.icons as |icon|}}\n    {{d-icon icon}}\n  {{/each}}\n\n  {{#if this.selectKit.options.useHeaderFilter}}\n    <div class=\"select-kit-header--filter\">\n      {{#if this.selectedContent.length}}\n        {{#each this.selectedContent as |item|}}\n          {{component this.selectKit.options.selectedChoiceComponent item=item selectKit=this.selectKit}}\n        {{/each}}\n      {{/if}}\n\n      {{component this.selectKit.options.filterComponent selectKit=this.selectKit id=(concat this.selectKit.uniqueID \"-filter\") hidePlaceholderWithSelection=true}}\n    </div>\n  {{else}}\n    <MultiSelect::FormatSelectedContent @content={{or this.selectedContent this.selectKit.noneItem}} @selectKit={{this.selectKit}} />\n\n    {{d-icon this.caretIcon class=\"caret-icon\"}}\n  {{/if}}\n</div>", {
    moduleName: "select-kit/components/multi-select/multi-select-header.hbs"
});